mapper.select:
SELECT A.FUNDING_TITLE, A.START_DATE, A.END_DATE, C.MEMBER_EMAIL, C.MEMBER_ID FROM TBL_FUNDING A JOIN TBL_SELLER B ON (A.SELLER_CODE = B.SELLER_CODE) JOIN TBL_MEMBER C ON (B.MEMBER_CODE = C.MEMBER_CODE) WHERE A.START_DATE > SYSDATE
WHERE FUNDING_TITLE = #{ searchId }
ORDER BY START_DATE ASC OFFSET #{startRow} -1 ROWS FETCH NEXT (#{endRow} - #{startRow} + 1) ROWS ONLY

SELECT A.FUNDING_TITLE, A.START_DATE, A.END_DATE, C.MEMBER_EMAIL, C.MEMBER_ID FROM TBL_FUNDING A JOIN TBL_SELLER B ON (A.SELLER_CODE = B.SELLER_CODE) JOIN TBL_MEMBER C ON (B.MEMBER_CODE = C.MEMBER_CODE) WHERE A.START_DATE <= SYSDATE AND A.END_DATE > SYSDATE
WHERE FUNDING_TITLE = #{ searchId }
ORDER BY START_DATE ASC OFFSET #{startRow} -1 ROWS FETCH NEXT (#{endRow} - #{startRow} + 1) ROWS ONLY

SELECT A.FUNDING_TITLE, A.START_DATE, A.END_DATE, C.MEMBER_EMAIL, C.MEMBER_ID FROM TBL_FUNDING A JOIN TBL_SELLER B ON (A.SELLER_CODE = B.SELLER_CODE) JOIN TBL_MEMBER C ON (B.MEMBER_CODE = C.MEMBER_CODE) WHERE A.END_DATE < SYSDATE
WHERE FUNDING_TITLE = #{ searchId }
ORDER BY END_DATE ASC OFFSET #{startRow} -1 ROWS FETCH NEXT (#{endRow} - #{startRow} + 1) ROWS ONLY

SELECT A.FUNDING_TITLE, A.START_DATE, A.END_DATE, E.MEMBER_EMAIL, E.MEMBER_ID, C.FUNDING_STATUS_NAME FROM TBL_FUNDING A JOIN TBL_FUNDING_STATUSLIST B ON (A.FUNDING_CODE = B.FUNDING_CODE) JOIN TBL_FUNDING_STATUS C ON (B.FUNDING_STATUS_CODE = C.FUNDING_STATUS_CODE) JOIN TBL_SELLER D ON (A.SELLER_CODE = D.SELLER_CODE) JOIN TBL_MEMBER E ON (D.MEMBER_CODE = E.MEMBER_CODE) WHERE C.FUNDING_STATUS_NAME = '승인완료'
WHERE FUNDING_TITLE = #{ searchId }
ORDER BY CHANGE_DATE DESC OFFSET #{startRow} -1 ROWS FETCH NEXT (#{endRow} - #{startRow} + 1) ROWS ONLY

SELECT A.FUNDING_TITLE, A.START_DATE, A.END_DATE, E.MEMBER_EMAIL, E.MEMBER_ID, C.FUNDING_STATUS_NAME FROM TBL_FUNDING A JOIN TBL_FUNDING_STATUSLIST B ON (A.FUNDING_CODE = B.FUNDING_CODE) JOIN TBL_FUNDING_STATUS C ON (B.FUNDING_STATUS_CODE = C.FUNDING_STATUS_CODE) JOIN TBL_SELLER D ON (A.SELLER_CODE = D.SELLER_CODE) JOIN TBL_MEMBER E ON (D.MEMBER_CODE = E.MEMBER_CODE) WHERE C.FUNDING_STATUS_NAME = '승인대기'
WHERE FUNDING_TITLE = #{ searchId }
ORDER BY CHANGE_DATE DESC OFFSET #{startRow} -1 ROWS FETCH NEXT (#{endRow} - #{startRow} + 1) ROWS ONLY
