mapper.select:
SELECT B.RNUM, B.FUNDING_CODE, B.FUNDING_TITLE, B.FUNDING_SUMMARY, B.APPLICATION_DATE, B.START_DATE, B.FUNDING_GOAL_MONEY, B.END_DATE, B.LIKES, B.VIEWS, B.CATEGORY_CODE, B.CATEGORY_NAME, B.FILE_MODIFICATION_NAME FROM( SELECT ROWNUM RNUM, A.FUNDING_CODE, A.FUNDING_TITLE, A.FUNDING_SUMMARY, A.APPLICATION_DATE, A.START_DATE, A.FUNDING_GOAL_MONEY, A.END_DATE, A.LIKES, A.VIEWS, A.CATEGORY_CODE, A.CATEGORY_NAME, A.FILE_MODIFICATION_NAME FROM( SELECT TF.FUNDING_CODE, TF.FUNDING_TITLE, TF.FUNDING_SUMMARY, TF.APPLICATION_DATE, TF.START_DATE, TF.FUNDING_GOAL_MONEY, TF.END_DATE, TF.LIKES, TF.VIEWS, TC.CATEGORY_CODE, TC.CATEGORY_NAME, TFF.FILE_MODIFICATION_NAME FROM TBL_FUNDING TF JOIN TBL_CATEGORY TC on TF.CATEGORY_CODE = TC.CATEGORY_CODE JOIN TBL_FUNDING_FILE TFF on TF.FUNDING_CODE = TFF.FUNDING_CODE WHERE TFF.FILE_TYPE = '메인'
AND TC.CATEGORY_NAME = #{cri.searchType}
ORDER BY TF.VIEWS DESC
ORDER BY TF.LIKES DESC
ORDER BY TF.START_DATE DESC
)A
WHERE ROWNUM <= #{ cri.rowEnd }
) B WHERE B.RNUM >= #{ cri.rowStart } ORDER BY 1 ASC
