mapper.insert:
INSERT
  INTO TBL_LIKE_FUNDING_LIST
(
  FUNDING_CODE
, MEMBER_CODE
)
VALUES
(
  #{ fundingCode }
, #{ memberCode }
)

mapper.delete:
DELETE
  FROM TBL_LIKE_FUNDING_LIST
 WHERE FUNDING_CODE = #{ fundingCode }
   AND MEMBER_CODE = #{ memberCode }

mapper.select:
SELECT
       COUNT(*)
  FROM TBL_LIKE_FUNDING_LIST
 WHERE FUNDING_CODE = #{ code }
   AND MEMBER_CODE = #{ memberCode }

mapper.insert:
INSERT
  INTO TBL_FUNDING_REPORT
(
  REPORT_CODE
, REPORT_CONTENT
, REPORT_DATE
, FUNDING_CODE
, MEMBER_CODE
, CATEGORY_CODE
, REPORT_RESP_STATUS
, REPORT_TITLE
)
VALUES
(
  SEQ_FUNDING_REPORT_CODE.NEXTVAL
, #{content}
, DEFAULT
, #{fundingCode}
, #{memberCode}
, #{categoryCode}
, default
, #{title}
)

mapper.select:
SELECT
       COUNT(*)
  FROM TBL_FUNDING_REPORT
 WHERE FUNDING_CODE = #{ code }
   AND MEMBER_CODE = #{ memberCode }
