mapper.insert:
INSERT
  INTO TBL_FUNDING_OPTIONS
(
  FUNDING_OPTION_CODE
, OPTION_NAME
, OPTION_CONTENTS
, OPTION_PRICE
, LIMIT_QUANTITY
, OPTION_COLOR
, OPTION_SIZE
, DELIVER_DATE
, FUNDING_CODE
)
VALUES
(
  SEQ_FUNDING_OPTIONS_CODE.NEXTVAL
, #{ optionName }
, #{ optionContents }
, #{ optionPrice }
, #{ limitQuantity }
, #{ optionColor }
, #{ optionSize }
, #{ deliverDate }
, #{ currKey }
)

mapper.delete:
DELETE
  FROM TBL_FUNDING_OPTIONS
 WHERE OPTION_NAME = #{ optionName }
   AND OPTION_CONTENTS = #{ optionInfo }
   AND OPTION_SIZE = #{ optionSize }
   AND OPTION_COLOR = #{ optionColor }

mapper.update:
UPDATE TBL_FUNDING
   SET RETURN_ROLE = #{ refundRule }
 WHERE FUNDING_CODE = #{ fundingCode }
